import { makeAutoObservable } from "mobx"

export default class BookStore {
    constructor() {
        this._genres = [
            {id: 1, name: 'Comedy' },
            {id: 4, name: 'Шутки'},
            {id: 6, name: 'Drama'},
            {id: 7, name: 'triller'},
            {id: 9, name: 'trille23r'},
            {id: 10, name: 'trille21r'},
            {id: 11, name: 'trille22r'},
        ]
        this._autors = [
            {id: 1, name: 'Misha Holodnyak' },
            {id: 2, name: 'Taras Shevchenko'},
        ]
        this._books = [
            {id: 1, name: '12 cats in a hole', price: 10, rating: 0, img: '5ba356cd-3a15-471e-a1b2-9f354723c68e.jpg' },
            {id: 3, name: '12 chairs', price: 25, rating: 0, img: 'ba09b4cc-64a9-45d8-94dd-40e03aff399c.jpg' },
            {id: 4, name: '12 chairs 2', price: 25, rating: 0, img: '3ed8271c-cd88-470a-9105-f48c48f74aad.jpg' },
        ]
        this._selectedGenre = {}
        
        makeAutoObservable(this)
    }

    setGenres(genres){
        this._genres = genres
    }

    setAutors(autors){
        this._autors = autors
    }

    setBook (books){
        this._books = books
    }

    setSelectedGenre (genre) {
        this._selectedGenre = genre
    }

    get genres() {
        return this._genres
    }
    
    get autors() {
        return this._autors
    }

    get books() {
        return this._books
    }

    get selectedGenre() {
        return this._selectedGenre
    }
}
