import React from 'react';
import { Container, Col, Image, Row, Card, Button } from 'react-bootstrap';
import bigStar from '../assets/bigStar.png'

const BookPage = () => {
  const book = {id: 1, name: '12 cats in a hole', price: 10, rating: 3 }
  const descriptions = [
    {id:1, title: 'Жанр', description: 'Хуйня'},
    {id:2, title: 'Страниц', description: 'Дохуя'},
    {id:3, title: 'Автор', description: 'Долбаеб'},
    {id:4, title: 'Иди', description: 'Нахуй'}
  ]
  return (
    <Container className="mt-3">
      <Row>
      <Col md={4}>
      <Image width={300} height={300} src={book.img}/>
      </Col>      
      <Col md={4}>
        <Row className="d-flex flex-column align-items-center">
          <h2>{book.name}</h2>
          <div
            className="d-flex align-items-center justify-content-center"
            style={{background: `url(${bigStar}) no-repeat center center`, width:240, height: 240, backgroundSize: 'cover', fontSize:64}}>
            {book.rating}
          </div>
        </Row>
      </Col>
      <Col md={4}>
        <Card className="d-flex flex-column align-items-center justify-content-around"
                        style={{width: 300, height: 300, fontSize: 32, border: '5px solid lightgray'}}>
          <h3>{book.name}</h3>
          <Button variant='outline-dark'>Додати до кошику</Button>
        </Card>
      </Col>
      <Row className="d-flex flex-column m-3">
                <h1>Характеристики</h1>
                {descriptions.map((info, index) =>
                    <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10}}>
                        {info.title}: {info.description}
                    </Row>
                )}
            </Row>
      </Row>
      
    </Container>
  );
}

export default BookPage;
