import React from "react";
import { Card, Col } from "react-bootstrap";
import Image from "react-bootstrap/Image";
import Star from "../assets/star.png";

const BookItem = ({ book }) => {
    // Проверка, что book не является пустым и что свойство img существует
    if (!book || !book.img) {
        return null; // Если book пустой или img отсутствует, вернуть null или другое представление по вашему усмотрению
    }

    return (
        <Col md={4} className={"mt-3"}>
            <Card style={{ width: 150, cursor: 'pointer' }} border={"light"}>
                <Image width={150} height={150} src={book.img} alt={book.name} /> {/* Добавлено свойство alt для улучшения доступности */}
                <div className="d-flex justify-cjntent-between align-items-center">
                    <div>kiga...</div>
                    <div>{book.rating}</div>
                    <Image src={Star} />
                </div>
            </Card>
        </Col>
    )
}

export default BookItem;
