import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL; // Замените на URL вашего сервера

export const getBooksByReader = async (readerId) => {
  const response = await axios.get(`${API_URL}/books/reader/${readerId}`);
  return response.data;
};

export const getBookByCipher = async (cipher) => {
  const response = await axios.get(`${API_URL}/books/cipher/${cipher}`);
  return response.data;
};

export const getCipherByBookTitle = async (title) => {
  const response = await axios.get(`${API_URL}/books/title/${title}`);
  return response.data;
};

export const getBookAssignmentDate = async (bookId, readerId) => {
  const response = await axios.get(`${API_URL}/books/${bookId}/reader/${readerId}/assignment-date`);
  return response.data;
};

export const getReadersWithOldBooks = async () => {
  const response = await axios.get(`${API_URL}/readers/old-books`);
  return response.data;
};

export const getReadersWithFewBooks = async () => {
  const response = await axios.get(`${API_URL}/readers/few-books`);
  return response.data;
};

export const getNumberOfReaders = async () => {
  const response = await axios.get(`${API_URL}/readers/count`);
  return response.data;
};

export const addNewReader = async (readerData) => {
  const response = await axios.post(`${API_URL}/readers`, readerData);
  return response.data;
};

export const removeOldBook = async (bookId) => {
  const response = await axios.delete(`${API_URL}/books/${bookId}`);
  return response.data;
};
