import React, { useContext, useEffect } from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import GenreBar from '../components/GenreBar';
import BookList from '../components/BookList.js';
import { observer } from 'mobx-react-lite';
import { Context } from '../index.js';
import {fetchBrands, fetchDevices, fetchTypes} from "../http/api.js";


const Shop = observer(() => {
  const {book} = useContext(Context)

  useEffect(() => {
    fetchTypes().then(data => book.setGenre(data))
    fetchDevices(null, null, 1, 2).then(data => {
      book.setDevices(data.rows)
      book.setTotalCount(data.count)
    })
}, [])

useEffect(() => {
    fetchDevices(book.selectedType.id, book.selectedBrand.id, book.page, 2).then(data => {
      book.setDevices(data.rows)
      book.setTotalCount(data.count)
    })
}, [book.page, book.selectedType, book.selectedBrand,])
  return (
    <Container >
        <Row className="mt-2">
          <Col md={3}>
            <GenreBar/>
          </Col>
          <Col md={9} className='d-flex flex-row align-items-start'>
            <BookList/>
          </Col>

        </Row>
    </Container>
  );
});

export default Shop;
