import React, { useContext } from 'react';
import { Context } from '../index';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import {Button} from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { ADMIN_ROUTE, SHOP_ROUTE } from '../utils/const';
import { observer } from 'mobx-react-lite';
import {useNavigate} from 'react-router-dom';

const NavBar = observer( () => {
    const {user} = useContext(Context)
    const navigate = useNavigate();
    return (
        <Navbar bg="dark" data-bs-theme="dark">
        <Container>
            <NavLink style={{color: 'whitesmoke'}} to={SHOP_ROUTE}>12 Книг та Один Кіт</NavLink>
          {user.isAuth ?
            <Nav className="ml-auto" style={{color: 'white'}}>
                <Button variant={"outline-light"} onClick={() => navigate(ADMIN_ROUTE)}>Адмін панель</Button>
                <Button variant={"outline-light"} onClick={() => navigate(ADMIN_ROUTE)} className={"ms-2"}>Вийти</Button>
            </Nav>
            :
            <Nav className="ml-auto" style={{color: 'white'}}>
                <Button variant={"outline-light"} onClick={() => user.setIsAuth(true)}>Авторизація</Button>
            </Nav>
        }
          
        </Container>
      </Navbar>

    );
});

export default NavBar;
