import React from 'react';
import { Button, Container } from "react-bootstrap";

const Admin = () => {
  const handleGetBooksByReader = () => {
    // Функция для получения списка книг за определенным читателем
  };

  const handleGetBookByCipher = () => {
    // Функция для получения названия книги по заданному шифру
  };

  const handleGetCipherByBookTitle = () => {
    // Функция для получения шифра книги по заданной названию
  };

  const handleGetBookAssignmentDate = () => {
    // Функция для получения даты закрепления книги за читателем
  };

  const handleGetReadersWithOldBooks = () => {
    // Функция для получения читателей, взявших книгу более месяца назад
  };

  const handleGetReadersWithFewBooks = () => {
    // Функция для получения читателей, за которыми закреплены книги в количестве <= 2
  };

  const handleGetNumberOfReaders = () => {
    // Функция для получения количества читателей
  };

  const handleAddNewReader = () => {
    // Функция для добавления нового читателя
  };

  const handleRemoveOldBook = () => {
    // Функция для списания старой или потерянной книги
  };

  return (
    <Container className="d-flex flex-column">
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetBooksByReader}
      >
        Які книги закріплені за певним читачем
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetBookByCipher}
      >
        Як називається книга із заданим шифром
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetCipherByBookTitle}
      >
        Який шифр книги із заданою назвою
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetBookAssignmentDate}
      >
        Коли книга була закріплена за читачем
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetReadersWithOldBooks}
      >
        Хто з читачів узяв книгу більше місяця тому
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetReadersWithFewBooks}
      >
        За ким з читачів закріплені книги, кількість примірників яких в бібліотеці не перевищує 2
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetNumberOfReaders}
      >
        Яке число читачів користується бібліотекою
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleAddNewReader}
      >
        Записати в бібліотеку нового читача
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleRemoveOldBook}
      >
        Списати стару або втрачену книгу
      </Button>
    </Container>
  );
};

export default Admin;
