import React from 'react';
import { Container, Col, Image, Row } from 'react-bootstrap';
import bigStar from '../assets/bigStar.png'
import { $mobx } from 'mobx';

const BookPage = () => {
  const book = {id: 1, name: '12 cats in a hole', price: 10, rating: 3 }
  return (
    <Container className="mt-3">
      <Col md={4}>
      <Image width={300} height={300} src={book.img}/>
      </Col>
      <Col md={4}>

      </Col>
      <Col md={4}>
        <Row>
          <h2>{book.name}</h2>
          <div className="d-flex align-items-centre justify-content-centre"
          style={{background: 'url(${bigStar}) no-repeat center center'}}>
            {book.rating}
          </div>
        </Row>
      </Col>
      <Col>

      </Col>
    </Container>
  );
}

export default BookPage;
