import React from 'react';
import {useNavigate} from 'react-router-dom';
import { Button, Container } from "react-bootstrap";
import {
  getBooksByReader,
  getBookByCipher,
  getCipherByBookTitle,
  getBookAssignmentDate,
  getReadersWithOldBooks,
  getReadersWithFewBooks,
  getNumberOfReaders,
  addNewReader,
  removeOldBook
} from '../http/api';

const Admin = () => {
  const navigate = useNavigate();

  const handleGetBooksByReader = async () => {
    const readerId = prompt("Введите ID читателя:");
    const data = await getBooksByReader(readerId);
    console.log(data);
  };

  const handleGetBookByCipher = async () => {
    const cipher = prompt("Введіть шифр книги:");
    const data = await getBookByCipher(cipher);    
      navigate('/book/' + data.id);
    
  };

  const handleGetCipherByBookTitle = async () => {
    const title = prompt("Введить назву книги:");
    const data = await getCipherByBookTitle(title);
    console.log(data);
    if (data && data.id) {
      //navigate('/book/' + data.id);
      prompt("Шифр заданої книги - " + data.id)
    } else {
      alert('Книга не найдена');
    }
  }; // переделать на то чтобы вылазила табличка с айдишником!

  const handleGetBookAssignmentDate = async () => {
    const bookId = prompt("Введите ID книги:");
    const readerId = prompt("Введите ID читателя:");
    const data = await getBookAssignmentDate(bookId, readerId);
    console.log(data);
  };

  const handleGetReadersWithOldBooks = async () => {
    const data = await getReadersWithOldBooks();
    console.log(data);
  };

  const handleGetReadersWithFewBooks = async () => {
    const data = await getReadersWithFewBooks();
    console.log(data);
  };

  const handleGetNumberOfReaders = async () => {
    const data = await getNumberOfReaders();
    console.log(data);
  };

  const handleAddNewReader = async () => {
    const name = prompt("Введите имя читателя:");
    const readerData = { name };
    const data = await addNewReader(readerData);
    console.log(data);
  };

  const handleRemoveOldBook = async () => {
    const bookId = prompt("Введите ID книги:");
    const data = await removeOldBook(bookId);
    console.log(data);
  };

  return (
    <Container className="d-flex flex-column">
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetBooksByReader}
      >
        Які книги закріплені за певним читачем
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetBookByCipher}
      >
        Як називається книга із заданим шифром
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetCipherByBookTitle}
      >
        Який шифр книги із заданою назвою
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetBookAssignmentDate}
      >
        Коли книга була закріплена за читачем
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 п-2"
        onClick={handleGetReadersWithOldBooks}
      >
        Хто з читачів узяв книгу більше місяця тому
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetReadersWithFewBooks}
      >
        За ким з читачів закріплені книги, кількість примірників яких в бібліотеці не перевищує 2
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleGetNumberOfReaders}
      >
        Яке число читачів користується бібліотекою
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleAddNewReader}
      >
        Записати в бібліотеку нового читача
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={handleRemoveOldBook}
      >
        Списати стару або втрачену книгу
      </Button>
      <Button variant="outline-dark" className="mb-3" onClick={fetchBooks}>
        Обновить таблицу
      </Button>
      <Button variant="outline-dark" className="mb-3" onClick={handleGetCipherByBookTitle}>
        Найти книгу по названию
      </Button>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Название</th>
            <th>Автор</th>
            <th>Жанр</th>
            <th>Шифр</th>
          </tr>
        </thead>
        <tbody>
          {books.map(book => (
            <tr key={book.id}>
              <td>{book.id}</td>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>{book.genre}</td>
              <td>{book.cipher}</td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Информация о книге</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedBook ? (
            <Table bordered>
              <tbody>
                <tr>
                  <td>ID</td>
                  <td>{selectedBook.id}</td>
                </tr>
                <tr>
                  <td>Название</td>
                  <td>{selectedBook.title}</td>
                </tr>
                <tr>
                  <td>Автор</td>
                  <td>{selectedBook.author}</td>
                </tr>
                <tr>
                  <td>Жанр</td>
                  <td>{selectedBook.genre}</td>
                </tr>
                <tr>
                  <td>Шифр</td>
                  <td>{selectedBook.cipher}</td>
                </tr>
              </tbody>
            </Table>
          ) : (
            <p>Книга не найдена.</p>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Закрыть
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
    
  );
  
};

export default Admin;
